    def caesar_decrypt_btn(self):
        try:
            cipher = self.lineEdit.text()
            key = self.lineEdit_2.text()
            key = int(key)
            if key > 0 and key < 26:
                step1, step2, step3, t = caesar.decrypt(cipher, key)
                self.label_7.setText(step1)
                self.label_8.setText(step2)
                self.label_9.setText(step3)
                self.label_10.setText(t)
            else:
                msg = QtWidgets.QMessageBox()
                msg.setWindowTitle('Value Error')
                msg.setText("The key must be greater than 0 and smaller than 26")
                x = msg.exec_()
        except:
            msg = QtWidgets.QMessageBox()
            msg.setWindowTitle('Error')
            msg.setText("All fields are required")
            x = msg.exec_()
